CREATE DATABASE pokedex;

USE pokedex;

CREATE TABLE entrenadores(
	id int PRIMARY KEY AUTO_INCREMENT,
    nombre varchar(200) not null unique,
    contraseña varchar(500) not null
);

CREATE TABLE tipos(
	id int PRIMARY KEY,
    tipo varchar(50)
);

CREATE TABLE pokemon(
	id int PRIMARY KEY,
    nombre varchar(200),
	tipo_1 int,
    tipo_2 int,
    url_imagen varchar(7000),
    descripcion varchar(2000),
    legendario tinyint not null,
    FOREIGN KEY (tipo_1) REFERENCES tipos(id),
    FOREIGN KEY (tipo_2) REFERENCES tipos(id)
);

CREATE TABLE plantillas(
	id_entrenador int PRIMARY KEY,
    slot1 int,
    slot2 int,
    slot3 int,
    slot4 int,
    slot5 int,
    slot6 int,
    FOREIGN KEY (id_entrenador) REFERENCES entrenadores(id),
    FOREIGN KEY (slot1) REFERENCES pokemon(id),
    FOREIGN KEY (slot2) REFERENCES pokemon(id),
    FOREIGN KEY (slot3) REFERENCES pokemon(id),
    FOREIGN KEY (slot4) REFERENCES pokemon(id),
    FOREIGN KEY (slot5) REFERENCES pokemon(id),
    FOREIGN KEY (slot6) REFERENCES pokemon(id)
);

CREATE TABLE stats(
	id_pokemon int PRIMARY KEY,
    PS int,
    ATK int,
    DEF int,
    VEL int,
    ATK_ESP int,
    DEF_ESP int,
    FOREIGN KEY (id_pokemon) REFERENCES pokemon(id)
);



